ISR(TIMER5_COMPA_vect) {
    uint8_t s=0, n=0;
    PORTB |= 0b01000000;
    for (; n<bytes_per_sample; n++) {
        s = next_byte();
        curr_sample++;
        if (curr_sample >= data_lengths[curr_chunk]) {
            // End of chunk
            curr_chunk++;
            curr_sample = 0;
            if (curr_chunk >= data_chunks) {
                // End of playback
                TIMSK5 = 0b00000000;
                end_read();
                TIFR5 |= 0b00000010;
                PORTB &= 0b01111111;
                enable_dtmf();
                finished = 1;
                return;
            }
            end_read();
            start_read(data_offsets[curr_chunk]);
            
            return;
        }
    }
    PORTA = s*volumes[curr_chunk]+128;
    PORTB &= 0b10111111;
}



